package com.Dec07;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int result;

// Ternary Operator consist 3 opearands statement ? True : false;
// Similar to If else statement

		result = a > b ? a + b : a - b;

		System.out.println("result of Two numbers is " + result);
	}

}
